import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [selectedMood, setSelectedMood] = useState("");
  const [aiResponse, setAiResponse] = useState("");
  const [currentPage, setCurrentPage] = useState("mood"); // "mood" or "journal"
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [journalEntry, setJournalEntry] = useState("");

  const journalPrompts = [
    "How was your day today?",
    "When did you feel the happiest this week?",
    "Share a happy memory that makes you smile",
    "What are you grateful for today?",
    "What's something you're looking forward to?",
    "What made you laugh today?",
    "Describe a moment that brought you peace",
    "What's a small win you celebrated today?"
  ];

  const getMoodResponse = (emoji) => {
    const responses = {
      "😎": "You're feeling awesome! That's great to hear. Keep that positive energy flowing!",
      "🙂": "Nice! A good mood is a great foundation for a productive day.",
      "😐": "Neutral is perfectly okay. Take a moment to reflect on what might lift your spirits.",
      "😔": "I hear you. Remember that it's okay to not be okay. Consider talking to someone you trust.",
      "😢": "I'm sorry you're feeling down. Remember that tough times don't last forever. Would you like to talk about it?"
    };
    return responses[emoji] || "";
  };

  const handleEmojiClick = (mood) => {
    setSelectedMood(`You selected: ${mood}`);
    setAiResponse(getMoodResponse(mood));
  };

  const handleJournalSubmit = (e) => {
    e.preventDefault();
    // Here you could add functionality to save the journal entry
    setJournalEntry("");
    setSelectedPrompt("");
    alert("Journal entry saved!"); // Placeholder for actual save functionality
  };

  const MoodPage = () => (
    <>
      <h1 style={{ 
        color: "#fff",
        fontSize: "2.5rem",
        marginBottom: "20px",
        fontWeight: "600"
      }}>Rate Your Mood</h1>
      <p style={{ 
        color: "#e0e0e0",
        fontSize: "1.2rem",
        marginBottom: "30px"
      }}>How are you feeling today?</p>

      <div style={{ 
        display: "flex", 
        justifyContent: "center", 
        gap: "20px", 
        margin: "40px 0",
        flexWrap: "wrap"
      }}>
        {["😎", "🙂", "😐", "😔", "😢"].map((emoji) => (
          <div
            key={emoji}
            onClick={() => handleEmojiClick(emoji)}
            style={{
              fontSize: "3.5rem",
              cursor: "pointer",
              transition: "all 0.3s ease",
              padding: "15px",
              borderRadius: "50%",
              background: "rgba(60, 60, 60, 0.8)",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
              width: "80px",
              height: "80px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "3px solid transparent",
              position: "relative",
              transform: selectedMood.includes(emoji) ? "scale(1.1)" : "scale(1)"
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.transform = "scale(1.2) translateY(-5px)";
              e.currentTarget.style.boxShadow = "0 10px 20px rgba(0, 0, 0, 0.3)";
              e.currentTarget.style.border = "3px solid #ff6b6b";
              e.currentTarget.style.background = "rgba(70, 70, 70, 0.9)";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.transform = selectedMood.includes(emoji) ? "scale(1.1)" : "scale(1)";
              e.currentTarget.style.boxShadow = "0 4px 15px rgba(0, 0, 0, 0.2)";
              e.currentTarget.style.border = "3px solid transparent";
              e.currentTarget.style.background = "rgba(60, 60, 60, 0.8)";
            }}
          >
            {emoji}
          </div>
        ))}
      </div>

      {(selectedMood || aiResponse) && (
        <div style={{ 
          marginTop: "40px",
          padding: "30px",
          background: "linear-gradient(135deg, #2d2d2d 0%, #353535 100%)",
          borderRadius: "15px",
          boxShadow: "0 5px 15px rgba(0, 0, 0, 0.2)",
          transform: "translateY(0)",
          animation: "fadeIn 0.5s ease-out",
          border: "1px solid rgba(255, 255, 255, 0.1)"
        }}>
          {selectedMood && (
            <p style={{ 
              fontSize: "1.4rem", 
              color: "#fff",
              marginBottom: "20px",
              fontWeight: "500"
            }}>
              {selectedMood}
            </p>
          )}
          {aiResponse && (
            <div style={{ 
              padding: "20px",
              backgroundColor: "rgba(80, 80, 80, 0.8)",
              borderRadius: "12px",
              marginTop: "15px",
              border: "2px solid #ff6b6b",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)"
            }}>
              <p style={{ 
                color: "#fff",
                fontSize: "1.2rem",
                lineHeight: "1.6",
                margin: 0
              }}>
                {aiResponse}
              </p>
            </div>
          )}
        </div>
      )}
    </>
  );

  const JournalPage = () => (
    <>
      <h1 style={{ 
        color: "#fff",
        fontSize: "2.5rem",
        marginBottom: "20px",
        fontWeight: "600"
      }}>Journal Your Thoughts</h1>

      <div style={{
        marginTop: "30px",
        marginBottom: "30px"
      }}>
        <select
          value={selectedPrompt}
          onChange={(e) => setSelectedPrompt(e.target.value)}
          style={{
            width: "80%",
            padding: "15px",
            fontSize: "1.1rem",
            backgroundColor: "rgba(60, 60, 60, 0.8)",
            color: "#fff",
            border: "2px solid #ff6b6b",
            borderRadius: "10px",
            cursor: "pointer",
            outline: "none"
          }}
        >
          <option value="">Select a prompt...</option>
          {journalPrompts.map((prompt, index) => (
            <option key={index} value={prompt}>{prompt}</option>
          ))}
        </select>
      </div>

      {selectedPrompt && (
        <form onSubmit={handleJournalSubmit} style={{
          width: "100%",
          maxWidth: "800px",
          margin: "0 auto"
        }}>
          <textarea
            value={journalEntry}
            onChange={(e) => setJournalEntry(e.target.value)}
            placeholder="Write your thoughts here..."
            style={{
              width: "100%",
              minHeight: "200px",
              padding: "20px",
              fontSize: "1.1rem",
              backgroundColor: "rgba(60, 60, 60, 0.8)",
              color: "#fff",
              border: "2px solid #ff6b6b",
              borderRadius: "10px",
              marginBottom: "20px",
              resize: "vertical",
              outline: "none"
            }}
          />
          <button
            type="submit"
            style={{
              padding: "12px 30px",
              fontSize: "1.1rem",
              backgroundColor: "#ff6b6b",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "all 0.3s ease",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)"
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.backgroundColor = "#ff5252";
              e.currentTarget.style.transform = "translateY(-2px)";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.backgroundColor = "#ff6b6b";
              e.currentTarget.style.transform = "translateY(0)";
            }}
          >
            Save Entry
          </button>
        </form>
      )}
    </>
  );

  return (
    <div style={{ 
      minHeight: "100vh",
      textAlign: "center", 
      padding: "40px 20px",
      background: "linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      <div style={{
        maxWidth: "800px",
        margin: "0 auto",
        padding: "30px",
        backgroundColor: "rgba(45, 45, 45, 0.9)",
        borderRadius: "20px",
        boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)",
        border: "1px solid rgba(255, 255, 255, 0.1)"
      }}>
        <div style={{
          display: "flex",
          justifyContent: "center",
          gap: "20px",
          marginBottom: "40px"
        }}>
          <button
            onClick={() => setCurrentPage("mood")}
            style={{
              padding: "10px 20px",
              fontSize: "1.1rem",
              backgroundColor: currentPage === "mood" ? "#ff6b6b" : "rgba(60, 60, 60, 0.8)",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "all 0.3s ease"
            }}
          >
            Mood Rating
          </button>
          <button
            onClick={() => setCurrentPage("journal")}
            style={{
              padding: "10px 20px",
              fontSize: "1.1rem",
              backgroundColor: currentPage === "journal" ? "#ff6b6b" : "rgba(60, 60, 60, 0.8)",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "all 0.3s ease"
            }}
          >
            Journal
          </button>
        </div>

        {currentPage === "mood" ? <MoodPage /> : <JournalPage />}
      </div>
    </div>
  );
};

export default App;
