import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [selectedMood, setSelectedMood] = useState("");

  const handleEmojiClick = (mood) => {
    setSelectedMood(`You selected: ${mood}`);
  };

  return (
    <div style={{ textAlign: "center", padding: "20px", backgroundColor: "#f9f9f9" }}>
      <h1>Rate Your Mood</h1>
      <p>How are you feeling today?</p>

      <div style={{ display: "flex", justifyContent: "center", gap: "10px", margin: "20px 0" }}>
        {["😎", "🙂", "😐", "😔", "😢"].map((emoji) => (
          <button
            key={emoji}
            onClick={() => handleEmojiClick(emoji)}
            style={{
              fontSize: "2rem",
              background: "none",
              border: "none",
              cursor: "pointer",
              transition: "transform 0.2s",
            }}
            onMouseOver={(e) => (e.target.style.transform = "scale(1.2)")}
            onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
          >
            {emoji}
          </button>
        ))}
      </div>

      {/* Result */}
      <div style={{ marginTop: "30px", fontSize: "1.2rem", color: "#555" }}>
        {selectedMood && <p>{selectedMood}</p>}
      </div>
    </div>
  );
};

export default App;
