import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [selectedMood, setSelectedMood] = useState("");
  const [aiResponse, setAiResponse] = useState("");
  const [currentPage, setCurrentPage] = useState("mood"); // "mood", "journal", or "entries"
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [journalEntry, setJournalEntry] = useState("");

  const journalPrompts = [
    "How was your day today?",
    "When did you feel the happiest this week?",
    "Share a happy memory that makes you smile",
    "What are you grateful for today?",
    "What's something you're looking forward to?",
    "What made you laugh today?",
    "Describe a moment that brought you peace",
    "What's a small win you celebrated today?"
  ];

  // Mock data for past entries - in a real app, this would come from a database
  const [pastEntries, setPastEntries] = useState([
    {
      id: 1,
      date: "2024-01-12",
      prompt: "How was your day today?",
      entry: "Today was quite productive. I managed to complete all my tasks and even had time for a short walk.",
      mood: "ðŸ˜Ž"
    },
    {
      id: 2,
      date: "2024-01-11",
      prompt: "What made you laugh today?",
      entry: "Had a funny video call with my best friend. We couldn't stop laughing about old memories.",
      mood: "ðŸ™‚"
    }
  ]);

  const getMoodPrompt = (emoji) => {
    const prompts = {
      "ðŸ˜Ž": "You are a wise and enthusiastic mentor. The user is feeling great and confident. Share an inspiring quote or short poem that celebrates their positive energy and encourages them to spread this joy to others.",
      "ðŸ™‚": "You are a gentle friend. The user is feeling good. Share an uplifting quote or short poem that helps maintain this positive mood and reminds them of life's simple pleasures.",
      "ðŸ˜": "You are a thoughtful guide. The user is feeling neutral. Share a mindful quote or short poem that helps them find beauty in the present moment and possibly lift their spirits.",
      "ðŸ˜”": "You are a compassionate friend. The user is feeling down. Share a comforting quote or short poem about hope, resilience, and the temporary nature of difficult times.",
      "ðŸ˜¢": "You are a caring counselor. The user is feeling very sad. Share a gentle, understanding quote or short poem about healing, self-compassion, and the courage it takes to feel deeply."
    };
    return prompts[emoji] || "";
  };

  const getAIResponse = async (emoji) => {
    try {
      const apiKey = process.env.REACT_APP_OPENAI_API_KEY;
      if (!apiKey) {
        throw new Error('OpenAI API key is not configured');
      }

      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo",
          messages: [
            {
              role: "system",
              content: getMoodPrompt(emoji)
            },
            {
              role: "user",
              content: "Share something meaningful for my current mood."
            }
          ],
          max_tokens: 150,
          temperature: 0.7
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('OpenAI API Error:', errorData);
        throw new Error(`API call failed: ${errorData.error?.message || 'Unknown error'}`);
      }

      const data = await response.json();
      console.log('OpenAI Response:', data); // Temporary debug log

      if (!data.choices || !data.choices[0]?.message?.content) {
        throw new Error('Unexpected API response format');
      }

      return data.choices[0].message.content;
    } catch (error) {
      console.error('Error details:', error);
      return `Error getting AI response: ${error.message}`;
    }
  };

  const handleEmojiClick = async (mood) => {
    setSelectedMood(`You selected: ${mood}`);
    setAiResponse("Getting a thoughtful response for you...");
    const response = await getAIResponse(mood);
    setAiResponse(response);
  };

  const handleJournalSubmit = (e) => {
    e.preventDefault();
    const newEntry = {
      id: pastEntries.length + 1,
      date: new Date().toISOString().split('T')[0],
      prompt: selectedPrompt,
      entry: journalEntry,
      mood: selectedMood.split(": ")[1] || "ðŸ“"
    };
    setPastEntries([newEntry, ...pastEntries]);
    setJournalEntry("");
    setSelectedPrompt("");
    alert("Journal entry saved!");
  };

  const MoodPage = () => (
    <>
      <h1 style={{ 
        color: "#fff",
        fontSize: "2.5rem",
        marginBottom: "20px",
        fontWeight: "600"
      }}>Rate Your Mood</h1>
      <p style={{ 
        color: "#e0e0e0",
        fontSize: "1.2rem",
        marginBottom: "30px"
      }}>How are you feeling today?</p>

      <div style={{ 
        display: "flex", 
        justifyContent: "center", 
        gap: "20px", 
        margin: "40px 0",
        flexWrap: "wrap"
      }}>
        {["ðŸ˜Ž", "ðŸ™‚", "ðŸ˜", "ðŸ˜”", "ðŸ˜¢"].map((emoji) => (
          <div
            key={emoji}
            onClick={() => handleEmojiClick(emoji)}
            style={{
              fontSize: "3.5rem",
              cursor: "pointer",
              transition: "all 0.3s ease",
              padding: "15px",
              borderRadius: "50%",
              background: "rgba(60, 60, 60, 0.8)",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
              width: "80px",
              height: "80px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "3px solid transparent",
              position: "relative",
              transform: selectedMood.includes(emoji) ? "scale(1.1)" : "scale(1)"
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.transform = "scale(1.2) translateY(-5px)";
              e.currentTarget.style.boxShadow = "0 10px 20px rgba(0, 0, 0, 0.3)";
              e.currentTarget.style.border = "3px solid #ff6b6b";
              e.currentTarget.style.background = "rgba(70, 70, 70, 0.9)";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.transform = selectedMood.includes(emoji) ? "scale(1.1)" : "scale(1)";
              e.currentTarget.style.boxShadow = "0 4px 15px rgba(0, 0, 0, 0.2)";
              e.currentTarget.style.border = "3px solid transparent";
              e.currentTarget.style.background = "rgba(60, 60, 60, 0.8)";
            }}
          >
            {emoji}
          </div>
        ))}
      </div>

      {(selectedMood || aiResponse) && (
        <div style={{ 
          marginTop: "40px",
          padding: "30px",
          background: "linear-gradient(135deg, #2d2d2d 0%, #353535 100%)",
          borderRadius: "15px",
          boxShadow: "0 5px 15px rgba(0, 0, 0, 0.2)",
          transform: "translateY(0)",
          animation: "fadeIn 0.5s ease-out",
          border: "1px solid rgba(255, 255, 255, 0.1)"
        }}>
          {selectedMood && (
            <p style={{ 
              fontSize: "1.4rem", 
              color: "#fff",
              marginBottom: "20px",
              fontWeight: "500"
            }}>
              {selectedMood}
            </p>
          )}
          {aiResponse && (
            <div style={{ 
              padding: "20px",
              backgroundColor: "rgba(80, 80, 80, 0.8)",
              borderRadius: "12px",
              marginTop: "15px",
              border: "2px solid #ff6b6b",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)"
            }}>
              <p style={{ 
                color: "#fff",
                fontSize: "1.2rem",
                lineHeight: "1.6",
                margin: 0
              }}>
                {aiResponse}
              </p>
            </div>
          )}
        </div>
      )}
    </>
  );

  const JournalPage = () => (
    <>
      <h1 style={{ 
        color: "#fff",
        fontSize: "2.5rem",
        marginBottom: "20px",
        fontWeight: "600"
      }}>Journal Your Thoughts</h1>

      <div style={{
        marginTop: "30px",
        marginBottom: "30px"
      }}>
        <select
          value={selectedPrompt}
          onChange={(e) => setSelectedPrompt(e.target.value)}
          style={{
            width: "80%",
            padding: "15px",
            fontSize: "1.1rem",
            backgroundColor: "rgba(60, 60, 60, 0.8)",
            color: "#fff",
            border: "2px solid #ff6b6b",
            borderRadius: "10px",
            cursor: "pointer",
            outline: "none"
          }}
        >
          <option value="">Select a prompt...</option>
          {journalPrompts.map((prompt, index) => (
            <option key={index} value={prompt}>{prompt}</option>
          ))}
        </select>
      </div>

      {selectedPrompt && (
        <form onSubmit={handleJournalSubmit} style={{
          width: "100%",
          maxWidth: "800px",
          margin: "0 auto"
        }}>
          <textarea
            value={journalEntry}
            onChange={(e) => setJournalEntry(e.target.value)}
            placeholder="Write your thoughts here..."
            style={{
              width: "100%",
              minHeight: "200px",
              padding: "20px",
              fontSize: "1.1rem",
              backgroundColor: "rgba(60, 60, 60, 0.8)",
              color: "#fff",
              border: "2px solid #ff6b6b",
              borderRadius: "10px",
              marginBottom: "20px",
              resize: "vertical",
              outline: "none"
            }}
          />
          <button
            type="submit"
            style={{
              padding: "12px 30px",
              fontSize: "1.1rem",
              backgroundColor: "#ff6b6b",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "all 0.3s ease",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)"
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.backgroundColor = "#ff5252";
              e.currentTarget.style.transform = "translateY(-2px)";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.backgroundColor = "#ff6b6b";
              e.currentTarget.style.transform = "translateY(0)";
            }}
          >
            Save Entry
          </button>
        </form>
      )}
    </>
  );

  const EntriesPage = () => (
    <>
      <h1 style={{ 
        color: "#fff",
        fontSize: "2.5rem",
        marginBottom: "20px",
        fontWeight: "600"
      }}>Past Entries</h1>

      <div style={{
        display: "flex",
        flexDirection: "column",
        gap: "20px",
        padding: "20px 0"
      }}>
        {pastEntries.map((entry) => (
          <div key={entry.id} style={{
            padding: "25px",
            backgroundColor: "rgba(60, 60, 60, 0.8)",
            borderRadius: "15px",
            border: "1px solid rgba(255, 255, 255, 0.1)",
            textAlign: "left",
            animation: "fadeIn 0.5s ease-out"
          }}>
            <div style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: "15px"
            }}>
              <span style={{ color: "#ff6b6b", fontSize: "0.9rem" }}>{entry.date}</span>
              <span style={{ fontSize: "1.5rem" }}>{entry.mood}</span>
            </div>
            <p style={{ 
              color: "#e0e0e0", 
              fontSize: "1.1rem", 
              fontStyle: "italic",
              marginBottom: "10px"
            }}>
              "{entry.prompt}"
            </p>
            <p style={{ color: "#fff", fontSize: "1rem", lineHeight: "1.6" }}>
              {entry.entry}
            </p>
          </div>
        ))}
      </div>
    </>
  );

  return (
    <div style={{ 
      minHeight: "100vh",
      textAlign: "center", 
      padding: "40px 20px",
      background: "linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      {/* Navigation Tabs - Moved outside the main card */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        gap: "15px",
        maxWidth: "800px",
        margin: "0 auto 20px auto",
      }}>
        {["mood", "journal", "entries"].map((page) => (
          <button
            key={page}
            onClick={() => setCurrentPage(page)}
            style={{
              padding: "12px 25px",
              fontSize: "1.1rem",
              backgroundColor: currentPage === page ? "#ff6b6b" : "rgba(60, 60, 60, 0.8)",
              color: "#fff",
              border: "none",
              borderRadius: "12px",
              cursor: "pointer",
              transition: "all 0.3s ease",
              transform: currentPage === page ? "translateY(0)" : "translateY(5px)",
              boxShadow: currentPage === page 
                ? "0 4px 15px rgba(255, 107, 107, 0.3)" 
                : "0 4px 15px rgba(0, 0, 0, 0.2)",
            }}
            onMouseOver={(e) => {
              if (currentPage !== page) {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.backgroundColor = "rgba(80, 80, 80, 0.9)";
              }
            }}
            onMouseOut={(e) => {
              if (currentPage !== page) {
                e.currentTarget.style.transform = "translateY(5px)";
                e.currentTarget.style.backgroundColor = "rgba(60, 60, 60, 0.8)";
              }
            }}
          >
            {page.charAt(0).toUpperCase() + page.slice(1)}
          </button>
        ))}
      </div>

      {/* Main Content Card */}
      <div style={{
        maxWidth: "800px",
        margin: "0 auto",
        padding: "30px",
        backgroundColor: "rgba(45, 45, 45, 0.9)",
        borderRadius: "20px",
        boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)",
        border: "1px solid rgba(255, 255, 255, 0.1)"
      }}>
        {currentPage === "mood" && <MoodPage />}
        {currentPage === "journal" && <JournalPage />}
        {currentPage === "entries" && <EntriesPage />}
      </div>
    </div>
  );
};

export default App;
